$('body').on('submit','#send__feedback',function(e){e.preventDefault();let validForm=!0;$('#name__fb, #mail__fb, #msg__fb').keyup(function(){cleanError($(this))});if($('#msg__fb').val()==''){$('#msg__fb').addClass('input__error-border').focus();validForm=!1}
  if($('#mail__fb').val()==''){$('#mail__fb').addClass('input__error-border').focus();validForm=!1}
  else{if(validMail($('#mail__fb').val())==!1){$('#mail__fb').addClass('input__error-border').focus();validForm=!1}}
  if($('#name__fb').val()==''){$('#name__fb').addClass('input__error-border').focus();validForm=!1}
  if(validForm){showSpinner();$.ajax({type:"POST",cache:!1,url:"/utils/send-feedback.php",data:{name:$('#name__fb').val(),mail:$('#mail__fb').val(),msg:$('#msg__fb').val(),from_page:$('#from__fb').val()},dataType:"text",async:!0,success:function(data){hideSpinner();switch(data){case 'true':$('#name__fb, #mail__fb, #msg__fb').val('').removeClass('input__error-border');$('.background_popup, #send__feedback__true').addClass('show');break;case 'false':$('.background_popup, #send__feedback__false').addClass('show');break}}})}})